import uuid from 'uuid/dist/v1'

export const todoReducer = (state, action) => {
  switch (action.type) {
    case 'ADD_TODO':
      return [...state, {
        text: action.text,
        date: action.date,
        isCompleted: false,
        id: uuid()
      }]

    case 'DELETE_TODOS':
      return state.filter((todo) => todo.id !== action.id)

    case 'CLEAR_TODOS':
      return

    case 'SEARCH_TODO':
      return state.filter((todo) => todo.text.includes(action.search))

    default:
      return state
  }
}
