import React, { useReducer, createContext } from 'react'
import { todoReducer } from '../reducers/todoReducer'
import axios from 'axios'

// Initial state
const initialState = [
  { id: 1, text: 'study', date: "2020-07-02" },
  { id: 2, text: 'sleep', date: "2021-09-22" },
  { id: 3, text: 'prey', date: "2020-02-27" }
]

// create context
export const TodosContext = createContext(initialState);

// Provider component
export default function TodosContextProvider({ children }) {
  const [todos, dispatch] = useReducer(todoReducer, initialState)

  //Actions
  function AddTodos(text, date) {
    dispatch({
      type: 'ADD_TODO',
      payload: { text, date }
    })
  }

  function AddTodos(id) {
    dispatch({
      type: 'DELETE_TODO',
      payload: { text, date }
    })
  }

  return (
    <TodosContext.Provider value={{ todos, AddTodos }} >
      {children}
    </ TodosContext.Provider >
  )
}
