import React, { useState, useContext } from 'react'
import { TodosContext } from '../context/TodosContext'

export default function AddTodo() {
  const { dispatch } = useContext(TodosContext)
  const [text, setText] = useState('')
  const handleSubmit = (e) => {
    e.preventDefault()
    dispatch({ type: 'ADD_TODO', text })
    setText('')
  }
  return (
    <form className="m-5" onSubmit={handleSubmit}>
      <div className="form-group">
        <label>Todo: </label>
        <input type="text" value={text} onChange={(e) => setText(e.target.value)} className="form-control" />
      </div>
      <div className="form-group">
        <label>Due Date: </label>
        <input type="date" value={text} onChange={(e) => setDate(e.target.value)} className="form-control" />
      </div>
      <button className="btn btn-primary mx-2 ">Add Todo</button>
    </ form >
  )
}
