import uuid from 'uuid/dist/v1'

export const todoReducer = (state, action) => {
  switch (action.type) {

    case 'GET_TODOS':
      console.log(action.payload)
      return [
        action.payload.map((todo) =>
          [...state, {
            id: todo.id,
            text: todo.text,
            date: "2020-07-02"
          }]
        )
      ]


    case 'ADD_TODO':
      return [...state, {
        id: action.payload.id,
        text: action.payload.text,
        date: action.payload.date
      }]

    case 'EDIT_TODO':
      return state.filter((todo) => todo.id !== action.payload.id)

    case 'DELETE_TODO':
      return state.filter((todo) => todo.id !== action.payload.id)

    case 'SEARCH_TODO':
      return state.filter((todo) => todo.text.includes(action.payload.search))

    default:
      return state
  }
}
