export const todoReducer = (state, action) => {
  switch (action.type) {

    case 'GET_TODOS':
      return {
        ...state,
        todos: action.payload

      }

    case 'ADD_TODO':
      return {
        ...state,
        todos: [{ id: uuid(), title: action.payload.newTodo.title }, ...state.todos]
      }

    //not finished
    case 'EDIT_TODO':
      return {
        ...state
      }
    case 'DELETE_TODO':
      return {
        ...state,
        todos: state.todos.filter((todo) => todo._id !== action.payload._id)
      }
    case 'SEARCH_TODO':
      return {
        ...state,
        todos: state.todos.filter((todo) => todo.title.includes(action.payload.search))
      }
    default:
      return state
  }
}
