import React, { useContext, useState } from 'react'
import { Link } from "react-router-dom";
import { TodosContext } from '../contexts/TodosContext'
import { ThemeContext } from '../contexts/ThemeContext'



export default function Navbar() {
  const { dispatch } = useContext(TodosContext)
  const [search, setSearch] = useState('')
  const handleSubmit = (e) => {
    e.preventDefault()
    dispatch({ type: 'SEARCH_TODO', search })
    setSearch('')
  }
  const { theme, setTheme } = useContext(ThemeContext)
  const mainColor = theme ? "success" : "dark"
  const background = theme ? "dark" : "muted"

  return (
    <nav className={`navbar navbar-${background}  bg-${background}`}>
      <p className={`navbar-brand text-${mainColor}`}>My Todo List</p>
      <p onClick={() => setTheme(!theme)} className={`text-${mainColor}`}>Change Theme</p>

      <div>
        <Link to="/">Home</Link>
        <Link to="/about">About</Link>
        <Link to="/todos">Todos</Link>
      </div>



      <form className="form-inline" onSubmit={handleSubmit}>
        <input className="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search" value={search} onChange={(e) => setSearch(e.target.value)} />
        <button className={`btn btn-outline-${mainColor} my-2 my-sm-0`} type="submit">Search</button>
      </form>
    </ nav >
  )
}
