import React, { useContext, useState } from 'react'
import { TodosContext } from '../context/TodosContext'
import { ThemeContext } from '../context/ThemeContext'

export default function Navbar() {
  const { dispatch } = useContext(TodosContext)
  const [search, setSearch] = useState('')
  const handleSubmit = (e) => {
    e.preventDefault()
    dispatch({ type: 'SEARCH_TODO', search })
    setSearch('')
  }
  const { theme, setTheme } = useContext(ThemeContext)
  const mainColor = theme ? "light" : "dark"
  console.log(theme)
  return (
    <nav className={`navbar navbar-${mainColor} bg-dark justify-content-between`}>
      <p className={theme ? "navbar-brand text-success" : "navbar-brand text-dark"}>My Todo List</p>
      <p onClick={() => setTheme(!theme)}>Change Theme</p>
      <form className="form-inline" onSubmit={handleSubmit}>
        <input className="form-control mr-sm-2" type="search" placeholder="Search" aria-label="Search" value={search} onChange={(e) => setSearch(e.target.value)} />
        <button className="btn btn-outline-success my-2 my-sm-0" type="submit">Search</button>
      </form>
    </ nav >
  )
}
