import React, { useContext } from 'react'
import { TodosContext } from '../context/TodosContext'


export default function Todo({ todo }) {
  const { dispatch } = useContext(TodosContext)
  return (

    <tr onClick={() => todo.isCompleted = !todo.isCompleted}>
      <td className="delete" onClick={() => dispatch({ type: 'DELETE_TODOS', id: todo.id })}>x</td>
      <td>{todo.text}</td>
      <td>{todo.date}</td>
    </tr>
  )
}
