//import uuid from 'uuid/v1'

export const todoReducer = (state, action) => {
  switch (action.type) {
    case 'ADD_TODO':
      return [...state, {
        text: action.text,
        isCompleted: false,
        id: uuid()
      }]

    case 'Remove_TODO':
      return state.filter((todo) => todo.id !== action.id)

    case 'SEARCH_TODO':
      return state.filter((todo) => todo.text.includes(action.search))

    default:
      return state
  }
}
