import React, { useState, useContext } from 'react'
import { TodosContext } from '../contexts/TodosContext'
import { ThemeContext } from '../contexts/ThemeContext'

export default function AddTodo() {
  const { dispatch } = useContext(TodosContext)
  const [text, setText] = useState('')
  const [date, setDate] = useState('')
  const handleSubmit = (e) => {
    e.preventDefault()
    dispatch({ type: 'ADD_TODO', text, date })
    setText('')
  }
  const { theme } = useContext(ThemeContext)
  const mainColor = theme ? "success" : "dark"
  const background = theme ? "dark" : "muted"
  return (
    <form className={`bg-${background} text-${mainColor}`} onSubmit={handleSubmit}>
      <div className="form-group">
        <label>Todo: </label>
        <input type="text" value={text} required placeholder="Enter the Todo.." onChange={(e) => setText(e.target.value)} className="form-control" />
      </div>
      <div className="form-group">
        <label>Due Date: </label>
        <input type="date" value={date} required onChange={(e) => setDate(e.target.value)} className="form-control" />
      </div>
      <button className="btn btn-primary mx-2 ">Add Todo</button>
    </ form >
  )
}
